mapper.select:
 	SELECT COUNT(*) FROM market
	WHERE mk_writer=#{userNo}
	AND mk_state != 2

  	SELECT * FROM (
	  		SELECT rownum no, t.*
	  		FROM (
				SELECT * FROM mk_comm
				WHERE mk_user_no = #{userNo}
				ORDER BY mk_comm_no DESC
			) t
			ORDER BY no ) x
		WHERE no between #{paging.startNo} AND #{paging.endNo}
