mapper.select:
select count(*) from (
select * from FIND_LESSON

)

select * from (
	select rownum no, t.*	
		FROM(
		SELECT * FROM FIND_LESSON
		WHERE user_no = #{loginUser.userNo}
		AND pay_state = 1
		ORDER BY lesson_no desc
		)t
	order by no)x
where no between #{paging.startNo} and #{paging.endNo}

mapper.update:
update find_lesson
set lesson_state = 0
where lesson_no = #{lessonNo}
