mapper.update:
update membership 



		, user_pw = #{userPw}


		, user_name = #{userName}


		, postcode = #{postCode}


		, roadaddress =  #{roadAddress}


		, detailaddress = #{detailAddress}


		, user_phone = #{userPhone}


		, user_gender =  #{userGender}


		, user_birth = #{userBirth}



where user_no = #{userNo}

mapper.select:
select * from teacherFile
where user_no = #{userNo}
and tch_file_rename like '%' || 'profile' || '%'

mapper.delete:
delete teacherfile
where user_No = #{userNo}
and tch_file_rename like '%' || 'profile' || '%'

mapper.select:
select count(*) from connected_lesson
where teacher = #{userNo}
and is_end = 0

mapper.update:
update membership
set is_leave = 1
where user_no = #{userNo}
